import React, { useEffect, useState } from 'react';
import TodoList from '../components/TodoList';
import Todo from '../components/Todo';
import FormInput from '../components/FormInput';
import axios from 'axios';
import { Button, Container, Form } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';

const Home = () => {
    const [todo, setTodo] = useState([]);
    const [input, setInput] = useState([]);
    const navigate = useNavigate();

    const getData = async() => {
        var config = {
            method: 'get',
            url: 'https://api.todoist.com/rest/v2/tasks',
            headers: { 
                'Authorization': 'Bearer eee262c83529a3e7fe8e0c11ebd5df2260af33e1', 
            }
        };
    await axios(config)
        .then((response) => {
            setTodo(response.data)
        })
    };

    useEffect(() => {
        getData();
        handleChange();
        handleSubmit();
    }, []);

    const handleInput = () => setInput(true);
    const handleClose = () => setInput(false);

    const [todos, setTodos] = useState();
    const [desc, setDesc] = useState();
    const [edit, setEdit] = useState();

    const handleSubmit = () => {
        var data = {
            content: todos,
            description: desc,
            due: edit
        };
        console.log(data)

        var config = {
            method: 'post',
            url: 'https://api.todoist.com/rest/v2/tasks',
            headers: { 
                'Authorization': 'Bearer eee262c83529a3e7fe8e0c11ebd5df2260af33e1',
                'Content-Type': 'application/json', 
            },
            data : data
        };
        axios(config)
            .then((response) => {
                console.log(response.data);
                getData();
            })
    }
    const handleTodos = (e) => {
        setTodos(e.target.value);
    };
    const handleDesc = (e) => {
        setDesc(e.target.value);
    };
    const handleEdit = (e) => {
        setEdit(e.target.value);
    };

    const handleChange = (id) => {
        var config = {
            method: 'delete',
            url: 'https://api.todoist.com/rest/v2/tasks/${id}',
            headers: { 
                'Authorization': 'Bearer eee262c83529a3e7fe8e0c11ebd5df2260af33e1', }
        };
        axios(config)
            .then((response) => {
                console.log(response.data);
                getData();
            });
    }

    const toDetail = (item) => {
        navigate('/detail', {
            state: {
                content: item.content,
                description: item.description,
                create: item.create,
                id: item.id
            }
        })
    }

  return (
    <Container>
        <h1>What's the Plan for Today?</h1>
        <FormInput />
        <TodoList 
            show={show}
            handleClose={handleClose}
            submit={handleSubmit}
            handleTodos={handleTodos}
            handleDesc={handleDesc}
            handleEdit={handleEdit}
        />
        <div>
            {todo.map((item) =>{
            return (
                <Todo
                content={item.content}
                delete={() => handleChange(item.id)}
                detail={() => toDetail(item)}
                />
            )
        })}
        </div>
    </Container>
  )
}

export default Home;
